import React, { useState } from 'react';

const CourseTile = ({ title, duration, description, outcomes, topics, isOpen, onClick }) => {
  return (
    <div className="bg-white p-6 rounded shadow-md mb-4">
      <div className="flex justify-between items-center cursor-pointer" onClick={onClick}>
        <h2 className="text-2xl font-bold text-green">{title}</h2>
        <span>{isOpen ? '-' : '+'}</span>
      </div>
      {isOpen && (
        <div className="mt-4">
          <p className="text-md mb-2">Duration: {duration}</p>
          <p className="text-md mb-4">{description}</p>
          <ul className="list-disc list-inside mb-4">
            {topics.map((topic, index) => (
              <li key={index}>{topic}</li>
            ))}
          </ul>
          <p>{outcomes}</p>
        </div>
      )}
    </div>
  );
};

export default CourseTile;
