import React, { useState } from 'react';

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
    course: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Form data submitted:', formData);
    // Reset form
    setFormData({
      name: '',
      email: '',
      message: '',
      course: ''
    });
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md">
      <h2 className="text-2xl font-bold mb-4">Register Your Interest</h2>
      <div className="mb-4">
        <label className="block text-gray-700">Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className="w-full px-3 py-2 border rounded"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700">Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className="w-full px-3 py-2 border rounded"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700">Message</label>
        <textarea
          name="message"
          value={formData.message}
          onChange={handleChange}
          className="w-full px-3 py-2 border rounded"
          rows="4"
          required
        ></textarea>
      </div>
      <div className="mb-4">
        <label className="block text-gray-700">Select Course</label>
        <select
          name="course"
          value={formData.course}
          onChange={handleChange}
          className="w-full px-3 py-2 border rounded"
          required
        >
          <option value="">Select a course</option>
          <option value="AI and Data Science Fundamentals">AI and Data Science Fundamentals</option>
          <option value="AI Engineering Advanced">AI Engineering Advanced</option>
          <option value="ML Deployment Advanced">ML Deployment Advanced</option>
        </select>
      </div>
      <button type="submit" className="bg-green text-white px-4 py-2 rounded hover:bg-yellowCream hover:text-green">
        Submit
      </button>
    </form>
  );
};

export default RegistrationForm;
