import React, { useState } from 'react';
import axios from 'axios';

const RegistrationForm = ({ selectedCourse }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    course: selectedCourse,
    message: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/send-email', formData);
      setSuccess(response.data);
      setError('');
    } catch (error) {
      setError('There was an error sending your enquiry. Please try again.');
      setSuccess('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2 className="text-2xl font-bold mb-4">Register Your Interest</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      {success && <p className="text-green-500 mb-4">{success}</p>}
      <div className="mb-4">
        <label className="block text-green mb-2">Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-2 border border-green rounded"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-green mb-2">Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className="w-full p-2 border border-green rounded"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-green mb-2">Course</label>
        <select
          name="course"
          value={formData.course}
          onChange={handleChange}
          className="w-full p-2 border border-green rounded"
          required
        >
          <option value="">Select a course</option>
          <option value="24-Week Bootcamps">24-Week Bootcamps</option>
          <option value="12-Week Skill-ups">12-Week Skill-ups</option>
          <option value="6-Week Courses">6-Week Courses</option>
          <option value="1-Week Courses">1-Week Courses</option>
          <option value="1-Day Workshops">1-Day Workshops</option>
        </select>
      </div>
      <div className="mb-4">
        <label className="block text-green mb-2">Message</label>
        <textarea
          name="message"
          value={formData.message}
          onChange={handleChange}
          className="w-full p-2 border border-green rounded"
          required
        />
      </div>
      <button
        type="submit"
        className="bg-green text-white px-4 py-2 rounded hover:bg-yellowCream hover:text-green"
      >
        Send Enquiry
      </button>
    </form>
  );
};

export default RegistrationForm;
