import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import CourseCard from './CourseCard';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import 'tailwindcss/tailwind.css';

const CoursesPage = () => {
  const [openCourse, setOpenCourse] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [searchTerm, setSearchTerm] = useState('');
  const location = useLocation();

  useEffect(() => {
    if (location.hash) {
      const element = document.getElementById(location.hash.substring(1));
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, [location]);

  const courses = [
    {
      id: 1,
      title: 'AI Basics in One Day',
      duration: '1 day',
      description: 'An intensive one-day course on AI basics.',
      outcomes: 'Understanding the fundamentals of AI.',
      topics: ['Introduction to AI', 'Basic Machine Learning Concepts', 'Real-world AI Applications'],
      nextStartDate: '2024-09-01',
      category: 'oneDay',
      cost: '$100'
    },
    // Add more courses here...
  ];

  const handleTileClick = (courseId) => {
    setOpenCourse(openCourse === courseId ? null : courseId);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredCourses = courses.filter(course =>
    course.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto px-6 py-12">
      <div className="flex justify-between items-center mb-8">
        <input
          type="text"
          placeholder="Search courses..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="px-4 py-2 border rounded-md w-full max-w-md"
        />
      </div>

      <div className="flex flex-col md:flex-row">
        <div className="flex-1">
          {['oneDay', 'oneWeek', 'sixWeek', 'twelveWeek', 'twentyFourWeek'].map(category => (
            <div key={category} id={category} className="mb-12">
              <h2 className="text-2xl font-bold mb-4 text-theme-color1 capitalize">{category.replace(/([A-Z])/g, ' $1').trim()} Courses</h2>
              {filteredCourses
                .filter(course => course.category === category)
                .map(course => (
                  <CourseCard
                    key={course.id}
                    course={course}
                    isOpen={openCourse === course.id}
                    onClick={() => handleTileClick(course.id)}
                  />
                ))}
            </div>
          ))}
        </div>

        <div className="flex-1 md:pl-12">
          <h2 className="text-2xl font-bold mb-4 text-theme-color1">Course Calendar</h2>
          <Calendar onChange={handleDateChange} value={selectedDate} />
        </div>
      </div>
    </div>
  );
};

export default CoursesPage;
