import React from 'react';

const CourseTile = ({ title, duration, description, outcomes, topics, isOpen, onClick, nextStartDate }) => {
  return (
    <div className="mb-4 p-4 bg-white rounded shadow-md hover:shadow-lg transition-shadow duration-200 cursor-pointer" onClick={onClick}>
      <h3 className="text-xl font-bold text-theme-color1 mb-2">{title}</h3>
      <p className="text-theme-color3"><strong>Duration:</strong> {duration}</p>
      <p className="text-theme-color3"><strong>Description:</strong> {description}</p>
      {isOpen && (
        <div className="mt-4">
          <p className="text-theme-color3"><strong>Outcomes:</strong> {outcomes}</p>
          <p className="text-theme-color3"><strong>Topics:</strong></p>
          <ul className="list-disc list-inside text-theme-color3">
            {topics.map((topic, index) => (
              <li key={index}>{topic}</li>
            ))}
          </ul>
          <p className="text-theme-color3"><strong>Next Start Date:</strong> {nextStartDate.toDateString()}</p>
        </div>
      )}
    </div>
  );
};

export default CourseTile;
