import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Autocomplete from 'react-autocomplete';

const HomePage = () => {
  const courses = [
    { id: 1, label: 'AI Basics in One Day' },
    { id: 2, label: 'Introduction to Data Science' },
    { id: 3, label: 'Machine Learning Crash Course' },
    { id: 4, label: 'AI Engineering Advanced' },
    { id: 5, label: 'Python Developer Course' },
    { id: 6, label: 'AI and Data Science Fundamentals' }
  ];

  const [value, setValue] = useState('');
  const [selectedCourse, setSelectedCourse] = useState(null);

  const handleSelect = (value, item) => {
    setValue(value);
    setSelectedCourse(item);
  };

  return (
    <div className="container mx-auto px-6 py-12">
      <section className="text-center bg-yellowCream p-6 rounded shadow-md">
        <h1 className="text-4xl font-bold text-green mb-4">Find the best AI courses for your next leap</h1>
        <p className="text-lg text-green mb-6">A curated directory of courses and resources for building AI skills.</p>

        <Autocomplete
          items={courses}
          getItemValue={(item) => item.label}
          renderItem={(item, isHighlighted) =>
            <div key={item.id} className={`item ${isHighlighted ? 'bg-yellowCream' : ''}`}>
              {item.label}
            </div>
          }
          value={value}
          onChange={(e) => setValue(e.target.value)}
          onSelect={handleSelect}
          inputProps={{
            className: 'w-full px-3 py-2 border rounded mb-4',
            placeholder: 'Search for a course...'
          }}
          menuStyle={{
            borderRadius: '3px',
            boxShadow: '0 2px 12px rgba(0, 0, 0, 0.1)',
            background: 'rgba(255, 255, 255, 0.9)',
            padding: '2px 0',
            fontSize: '90%',
            position: 'fixed',
            overflow: 'auto',
            maxHeight: '50%',
            zIndex: '100',
          }}
        />

        <Link to="/contact">
          <button className="bg-green text-white px-4 py-2 rounded hover:bg-yellowCream hover:text-green">Get Started</button>
        </Link>
      </section>
    </div>
  );
};

export default HomePage;
