import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div className="container mx-auto px-6 py-12">
      <h1 className="text-4xl font-bold mb-4 text-green">Welcome to AI Tech Institute</h1>
      <p className="text-lg mb-6 text-green">Choose the course that best fits your needs and start learning today!</p>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Link to="/courses#one-day" className="block p-6 bg-white rounded shadow-md hover:bg-yellowCream transition">
          <h2 className="text-2xl font-bold text-green">1-Day Courses</h2>
        </Link>
        <Link to="/courses#one-week" className="block p-6 bg-white rounded shadow-md hover:bg-yellowCream transition">
          <h2 className="text-2xl font-bold text-green">1-Week Courses</h2>
        </Link>
        <Link to="/courses#six-week" className="block p-6 bg-white rounded shadow-md hover:bg-yellowCream transition">
          <h2 className="text-2xl font-bold text-green">6-Week Courses</h2>
        </Link>
        <Link to="/courses#twelve-week" className="block p-6 bg-white rounded shadow-md hover:bg-yellowCream transition">
          <h2 className="text-2xl font-bold text-green">12-Week Courses</h2>
        </Link>
        <Link to="/courses#twenty-four-week" className="block p-6 bg-white rounded shadow-md hover:bg-yellowCream transition">
          <h2 className="text-2xl font-bold text-green">24-Week Bootcamps</h2>
        </Link>
      </div>
    </div>
  );
};

export default HomePage;
