import React, { useState } from 'react';
import axios from 'axios';

const RegistrationForm = ({ selectedCourse }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    course: selectedCourse,
    message: ''
  });
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/send-email', formData);
      setSuccessMessage('Your enquiry has been sent successfully.');
      setErrorMessage('');
      setFormData({
        name: '',
        email: '',
        course: '',
        message: ''
      });
    } catch (error) {
      setErrorMessage('There was an error sending your enquiry. Please try again.');
      setSuccessMessage('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md">
      <h2 className="text-2xl font-bold mb-4">Register Your Interest</h2>
      {successMessage && <p className="text-green-500 mb-4">{successMessage}</p>}
      {errorMessage && <p className="text-red-500 mb-4">{errorMessage}</p>}
      <div className="mb-4">
        <label className="block text-green font-bold mb-2">Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded"
        />
      </div>
      <div className="mb-4">
        <label className="block text-green font-bold mb-2">Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded"
        />
      </div>
      <div className="mb-4">
        <label className="block text-green font-bold mb-2">Course</label>
        <select
          name="course"
          value={formData.course}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded"
        >
          <option value="">Select a course</option>
          <option value="AI Basics in One Day">AI Basics in One Day</option>
          <option value="Introduction to Data Science">Introduction to Data Science</option>
          <option value="Machine Learning Crash Course">Machine Learning Crash Course</option>
          <option value="AI Engineering Advanced">AI Engineering Advanced</option>
          <option value="Python Developer Course">Python Developer Course</option>
          <option value="AI and Data Science Fundamentals">AI and Data Science Fundamentals</option>
        </select>
      </div>
      <div className="mb-4">
        <label className="block text-green font-bold mb-2">Message</label>
        <textarea
          name="message"
          value={formData.message}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded"
        />
      </div>
      <button type="submit" className="bg-green text-white px-4 py-2 rounded hover:bg-yellowCream hover:text-green">Submit</button>
    </form>
  );
};

export default RegistrationForm;
