import React from 'react';
import { Link } from 'react-router-dom';

const CourseTile = ({ title, duration, description, outcomes, topics, isOpen, onClick, nextStartDate }) => {
  return (
    <div className={`course-tile ${isOpen ? 'open' : ''} transition-transform transform`} onClick={onClick}>
      <h3 className="text-xl font-bold text-theme-color1 mb-2">{title}</h3>
      <p className="text-theme-color3">{duration}</p>
      <p className="text-theme-color3">{description}</p>
      {isOpen && (
        <div className="mt-4">
          <h4 className="font-semibold text-theme-color3">Outcomes</h4>
          <p className="text-theme-color3">{outcomes}</p>
          <h4 className="font-semibold text-theme-color3 mt-2">Topics</h4>
          <ul className="list-disc list-inside text-theme-color3">
            {topics.map((topic, index) => (
              <li key={index}>{topic}</li>
            ))}
          </ul>
          <p className="text-theme-color3 mt-2">Next Start Date: {nextStartDate.toLocaleDateString()}</p>
          <Link to="/contact" className="mt-4 inline-block bg-theme-color1 text-white px-4 py-2 rounded hover:bg-theme-color2 hover:text-theme-color1">
            Enrol
          </Link>
        </div>
      )}
    </div>
  );
};

export default CourseTile;
